INSERT INTO `news` (`id`, `name`, `content`, `file`, `date`, `category_id`, `description`) VALUES
(33, 'مجلس دول الخليج يجدد دعم مغربية الصحراء', '<p dir=\"rtl\"><span style=\"font-size:18px\">أكد المجلس الأعلى لمجلس التعاون لدول الخليج العربية مواقفه وقراراته الثابتة الداعمة لمغربية الصحراء والحفاظ على أمن واستقرار المملكة المغربية ووحدة أراضيها.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/9UM9HU-preview.jpg\" style=\"height:428px; width:643px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">جاء ذلك في البيان الختامي الذي توج أعمال القمة الثانية والأربعين لمجلس التعاون الخليجي، التي انعقدت في الرياض اليوم الثلاثاء، برئاسة المملكة العربية السعودية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأشاد البيان الختامي للقمة، الذي تلاه نايف بن فلاح الحجرف، الأمين العام لمجلس التعاون لدول الخليج العربية، بقرار مجلس الأمن رقم 2602 الصادر بتاريخ 29 أكتوبر 2021 بشأن الصحراء المغربية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">كما أكد المجلس الأعلى أهمية الشراكة الاستراتيجية الخاصة بين مجلس التعاون والمملكة المغربية؛ وتنفيذ خطة العمل المشتركة.</span></p>\r\n', 0x6a616d6133612d363162393063383730356333382d39554d3948552d707265766965772e77656270, '2021-12-14 21:28:39', '1', 'أكد المجلس الأعلى لمجلس التعاون لدول الخليج العربية مواقفه وقراراته الثابتة الداعمة لمغربية الصحراء والحفاظ على أمن واستقرار المملكة المغربية ووحدة أراضيها.'),
(34, 'أبو زيد تترشح لقيادة حزب \"الاتحاد الاشتراكي\" عبر مفوض قضائي', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">كشفت مصادر مطلعة من حزب الاتحاد الاشتراكي للقوات الشعبية أن حسناء أبو زيد، البرلمانية السابقة والقيادية في حزب &ldquo;الوردة&rdquo;، قدمت، اليوم الثلاثاء، ترشيحها لمنصب الكتابة الأولى للحزب ذاته.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/hasnae-abouzayed.jpg\" style=\"height:463px; width:695px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأوضحت المصادر لهسبريس أن حسناء أبو زيد بعثت ملف ترشيحها عبر مفوض قضائي إلى المقر المركزي لحزب الاتحاد الاشتراكي للقوات الشعبية بالرباط خشية عدم تسلمه منها بسبب الخلاف الحاد بينها وبين إدريس لشكر، الكاتب الأول الحالي للحزب.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكانت أصوات اتحادية عديدة قد طالبت بترشح حسناء أبو زيد لقيادة الاتحاد الاشتراكي للقوات الشعبية؛ بينما اعتبر إدريس لشكر، في تصريحات إعلامية، أنها لم يعد يربطها شيء بالحزب.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ويعتبر ترشيح حسناء أبو زيد هو الترشيح الثالث الذي يعلن عنه رسميا داخل حزب &ldquo;الوردة&rdquo;، بعد إعلان القيادي الاتحادي عبد الكريم بنعتيق قراره الترشح وكذا القيادي محمد بوبكري.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكان إدريس لشكر، الكاتب الأول لحزب الاتحاد الاشتراكي للقوات الشعبية، أكد عدم رغبته في الترشح لقيادة الاتحاد مرة أخرى، خاصة أن قوانين الحزب لا تسمح بأكثر من ولايتين؛ إلا أنه عاد ليعلن في تصريحات أخرى أن المؤتمر هو المعني ببقائه من عدمه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ويواصل عدد من أنصار لشكر التعبئة في صفوف الحزب من أجل تعديل النظام الداخلي والتنصيص على إمكانية قيادة الكاتب الأول للحزب لثلاث ولايات متتالية؛ بينما يرفض آخرون هذا التوجه، محذرين من اختزال الحزب في شخص واحد.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكان المجلس الوطني لحزب الاتحاد الاشتراكي للقوات الشعبية قرر عقد المؤتمر الوطني أيام 28 و29 و30 يناير 2022 في بوزنيقة، حضوريا وعن بعد.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393063636437633763632d6861736e61652d61626f757a617965642e77656270, '2021-12-14 21:29:49', '1', 'كشفت مصادر مطلعة من حزب الاتحاد الاشتراكي للقوات الشعبية أن حسناء أبو زيد، البرلمانية السابقة والقيادية في حزب “الوردة”، قدمت، اليوم الثلاثاء، ترشيحها لمنصب الكتابة الأولى للحزب ذاته.'),
(35, 'الملك محمد السادس يستقبل سفراء جددا بالخارج', '<p dir=\"rtl\"><span style=\"font-size:18px\">استقبل الملك محمد السادس، اليوم الثلاثاء بالقصر الملكي بالرباط، عددا من السفراء الجدد بالبعثات الدبلوماسية للمملكة، الذين سلمهم ظهائر تعيينهم.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/ROI_MED6_Ambass.jpg\" style=\"height:403px; width:605px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويتعلق الأمر بكل من:</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">محمد بنشعبون، سفير المغرب لدى الجمهورية الفرنسية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">كريم مدرك، سفير المغرب لدى مملكة السويد. وجمهورية لاتفيا.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">الحسن الناصري، سفير&nbsp; لدى جمهورية السنغال وجمهورية غامبيا وجمهورية غينيا بيساو وجمهورية الرأس الأخضر.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">عبد الرزاق لعسل، سفير المغرب لدى جمهورية كينيا وجمهورية جنوب السودان.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">بشرى بودشيش، سفيرة المغرب لدى جمهورية بنما.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وسن الزَيلاشي، سفيرة لدى كومنولث أستراليا ونيوزيلاندا وتوفالو وجمهورية كيريباتي وبابوا غينيا الجديدة.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">علي ابن عيسى، سفير المملكة لدى دولة الكويت.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ادريس اسباين، سفير لدى جمهورية مالي.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">زكرياء الكوميري، سفير لدى جمهورية تنزانيا المتحدة وجمهورية القمر المتحدة.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">محمد بصري، سفير لدى مملكة الأراضي المنخفضة (هولندا).</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">محمد الصبيحي، سفير لدى جمهورية اليونان وجمهورية قبرص.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">عبد الرحيم رحالي، سفير لدى مملكة التايلاند.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">هشام دحان، سفير لدى جمهورية الدومينيكان وجمهورية هايتي.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">عبد الرحيم مزيان، سفير لدى دولة فلسطين.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">عصام طيب، سفير لدى جمهورية غينيا وجمهورية السيراليون.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">هشام العَلْوي، سفير لدى جمهورية كوبا.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">حكيم حجوي، سفير لدى المملكة المتحدة لبريطانيا العظمى وإيرلاندا الشمالية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">نبيلة فريدجي، سفيرة لدى مملكة النرويج وجمهورية إيسلندا.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">محمد رشيد معنينو، سفير لدى جمهورية كازاخستان وجمهورية طاجيكستان وتركمانستان وجمهورية قرغيزستان.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">إثر ذلك، أدى السفراء الجدد القسم بين يدي جلالة الملك، وقد حضر هذا الاستقبال وزير الشؤون الخارجية والتعاون الافريقي والمغاربة المقيمين بالخارج، ناصر بوريطة، والحاجب الملكي سيدي محمد العلوي</span></p>\r\n', 0x6a616d6133612d363162393064323862646365632d524f495f4d4544365f416d626173732e77656270, '2021-12-14 21:31:20', '1', 'استقبل الملك محمد السادس، اليوم الثلاثاء بالقصر الملكي بالرباط، عددا من السفراء الجدد بالبعثات الدبلوماسية للمملكة، الذين سلمهم ظهائر تعيينهم.'),
(36, 'الراشدي يستعرض جهود المملكة لوقف الفساد', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">قال أحمد بشير الراشدي، رئيس الهيئة الوطنية للنزاهة والوقاية من الرشوة ومحاربتها، إن جائحة كوفيد 19 وتداعياتها على تدبير الشأن العام تفسح المجال أمام مخاطر خلق بؤر فساد جديدة، وتقويض جهود مكافحته، لاسيما في ما يتعلق بالتحديات التي يمكن أن تواجه أجهزة الرقابة وسلطات إنفاذ القانون لاقتفاء أثر الممارسات الفاسدة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/mohamed-bachir-elrachidi2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأضاف الراشدي، ضمن أشغال الدورة التاسعة لمؤتمر الدول الأطراف في اتفاقية الأمم المتحدة لمكافحة الفساد، المنعقد بمصر، أن &ldquo;هذا المؤتمر يعتبر أكبر محفل دولي للتداول في قضايا الفساد، ولحظة فارقة للتفكير الجماعي&rdquo;، داعيا إلى &ldquo;وضع رؤية ملموسة لفتح آفاق جديدة أمام مختلف الدول لتطوير سياساتها وبرامجها الوطنية، من خلال مقاربة تدمج خصوصيات ومستلزمات تدبير الظروف الاستثنائية&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">واعتبر المتحدث، اليوم الثلاثاء، أن &ldquo;المملكة المغربية انخرطت بقناعة وقوة في الدينامية الدولية لمكافحة الفساد، من خلال تقديم الدعم الكامل للجهود الهادفة إلى تنزيل وتنفيذ أحكام اتفاقية الأمم المتحدة، عبر المشاركة النشطة في كل دورات هذا المؤتمر، وباحتضان دورته الرابعة التي تميزت بإقرار إعلان مراكش للوقاية من الفساد&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">&ldquo;في هذا الإطار، اعتمد المغرب مجموعة من التدابير، توجت بالعديد من المكتسبات القانونية والمؤسساتية التي تم تحقيقها منذ المراجعة الدستورية لسنة 2011، كاستقلال النيابة العامة، وإحداث مفتشية عامة للسلطة القضائية، وتقوية أدوار هيئات الحكامة والرقابة العليا&rdquo;، يردف الراشدي.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأشار المتحدث ذاته إلى أن &ldquo;المغرب وضع الإستراتيجية الوطنية لمكافحة الفساد 2016-2025، وبعد تقييم موضوعي تم تعزيز هيكلتها وتوجيهها لتشمل، ابتداء من سنة 2022، مجموعة من الأولويات، تهم على الخصوص التحول الرقمي، والرقابة والمساءلة، والطلبيات العمومية، ونزاهة القطاع الخاص؛ كما تم إصدار القانون المتعلق بالهيئة الوطنية للنزاهة والوقاية من الرشوة ومحاربتها باعتبارها هيئة دستورية مستقلة، ذات صلاحيات واسعة، سواء على مستوى تقديم التوجهات الإستراتيجية لسياسة الدولة في مجال الوقاية ومحاربة الفساد، والإشراف على تنسيقها وضمان تتبع تنفيذها، أو على مستوى القيام بعمليات البحث والتحري في قضايا الفساد&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وتعززت هذه السياسات، يقول الراشدي، بمصادقة الملك على النموذج التنموي الجديد، الذي يَنْبَني على مبدأ الموازاة بين &ldquo;دولة قوية&rdquo; و&rdquo;مجتمع قوي&rdquo;، وعلى &ldquo;أولوية المصلحة العامة، وتدعيم قيم الأخلاقيات والنزاهة&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">واعتبر المسؤول ذاته أن &ldquo;المملكة المغربية تطمح إلى أن تفضي أشغال هذه الدورة إلى اعتماد مشروع القرار الذي سيتم التقدم به بشأن الوقاية من الفساد، لكونه سيفتح أفقا جديدا لاستثمار إعلان مراكش، لاسيما ما يخص ملاءمة الإستراتيجيات الوطنية مع وضعيات الأزمات، وجعل الوقاية من الفساد رافعة في خطة التنمية المدمجة والمستدامة&rdquo;.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393064366435656362382d6d6f68616d65642d6261636869722d656c72616368696469322e77656270, '2021-12-14 21:32:29', '1', 'قال أحمد بشير الراشدي، رئيس الهيئة الوطنية للنزاهة والوقاية من الرشوة ومحاربتها، إن جائحة كوفيد 19 وتداعياتها على تدبير الشأن العام تفسح المجال أمام مخاطر خلق بؤر فساد جديدة، وتقويض جهود مكافحته، لاسيما في ما يتعلق بالتحدي'),
(37, 'بوبكري يترشح لخلافة لشكر على رأس \"الاتحاد\"', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">أعلن محمد بوبكري، عضو المكتب السياسي لحزب الاتحاد الاشتراكي للقوات الشعبية سابقا، ترشحه لمنصب الكاتب الأول للحزب.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/USFP-1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكشف بوبكري، في تصريح لهسبريس، أنه التقى صباح اليوم الكاتب الأول لحزب الاتحاد الاشتراكي للقوات الشعبية، إدريس لشكر، وأخبره بقراره الترشح لمنصب الكاتب الأول.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ويعتبر ترشيح بوبكري ثاني ترشيح يعلن عنه رسميا داخل الاتحاد الاشتراكي، بعد إعلان القيادي الاتحادي عبد الكريم بنعتيق قراره الترشح.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">من جهة أخرى، يروج داخل حزب الاتحاد الاشتراكي للقوات الشعبية أن حسناء أبو زيد، عضو المكتب السياسي للحزب سابقا، ستعلن بدورها ترشحها لمنصب الكتابة الأولى.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وبوبكري هو آخر رئيس للاتحاد الوطني لطلبة المغرب، كما شغل عضوية المكتب السياسي للاتحاد الاشتراكي قبل أن يستقيل منه عقب خلافات حادة مع الكاتب الأول، إدريس لشكر.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكان الكاتب الأول لحزب الاتحاد الاشتراكي للقوات الشعبية أكد عدم رغبته الترشح لقيادة الاتحاد مرة أخرى، خاصة أن قوانين الحزب لا تسمح بأكثر من ولايتين، إلا أنه عاد ليعلن في تصريحات أخرى أن المؤتمر هو المعني ببقائه من عدمه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ويواصل عدد من أنصار لشكر التعبئة في صفوف الحزب من أجل تعديل النظام الداخلي والتنصيص على إمكانية قيادة الكاتب الأول الحزب لثلاث ولايات متتالية، بينما يرفض آخرون هذا التوجه، محذرين من اختزال الحزب في شخص واحد.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكان المجلس الوطني لحزب الاتحاد الاشتراكي قرر عقد المؤتمر الوطني أيام 28 و29 و30 يناير 2022 في بوزنيقة، حضوريا وعن بعد.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393064643139303231662d555346502d312e77656270, '2021-12-14 21:34:09', '1', 'أعلن محمد بوبكري، عضو المكتب السياسي لحزب الاتحاد الاشتراكي للقوات الشعبية سابقا، ترشحه لمنصب الكاتب الأول للحزب.'),
(38, 'هذه تفاصيل عدم استقبال المملكة أعضاء \"مراسلون بلا حدود\" بسبب الصحراء‬', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">كشف أحمد شوقي بنيوب، المندوب الوزاري المكلف بحقوق الإنسان، عن مقاربة المملكة في تعاطيها مع المنظمات الحقوقية الدولية الناشطة في مجال حقوق الإنسان، مثل &ldquo;مراسلون بلا حدود&rdquo;، حيث ربط تعامل المغرب مع هذه المنظمات بطبيعة موقفها من نزاع الصحراء المغربية.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/05/sahara.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وشدد المسؤول الوزاري، خلال تقديمه لتقرير الاستعراض الخاص بحقوق الإنسان، صباح اليوم الثلاثاء، على أن &ldquo;منظمة مراسلون بلا حدود&rdquo; تخوض منذ سنوات حملة سياسية ضد المغرب ووحدته الترابية، حملة تفتقد كل مقومات المهنية والموضوعية المتعارف عليها في مجال حقوق الإنسان.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأورد بنيوب، في معرض كلامه، أن مؤشر حرية الصحافة التي تعتمده منظمة &ldquo;مراسلون بلا حدود&rdquo; غير جدير بالثقة والمصداقية العلمية والسند الحقوقي وغير مقنع، خاصة أنه يعتمد معايير ميكانيكية تتعسف في استعمال صيغ رياضية يصعب قياس حرية التعبير على ضوئها وفهم الظروف الحقيقية في كل دولة على ضوئها.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكشف المندوب الوزاري المكلف بحقوق الإنسان أنه تلقى طلبا من قبل منظمة &ldquo;مراسلون بلا حدود&rdquo; لعقد لقاء في نهاية شهر نونبر المنصرم، وقد اعتذر عن ذلك، &ldquo;انسجاما مع الباب المسدود الذي وصلت إليه العلاقة من جهة، ومن جهة أخرى لعدم أخذها الحياد اللازم من النزاع الإقليمي حول قضية الصحراء المغربية&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">من جهة أخرى، اعترف المسؤول ذاته بوجود أزمة تطور نظام حماية حقوق الإنسان، أزمة تقع في صلب معادلة التوازن بين حماية حقوق الإنسان وحفظ الأمن والنظام العام، أزمة تهم ترصيد المكتسبات والحفاظ عليها ومواصلة البناء.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ولاحظ بنيوب أن توصيات المجلس الوطني لحقوق الإنسان لم تحظ بالاهتمام المطلوب من طرف السلطتين الحكومية والبرلمانية، سواء تعلق الأمر بالحوار أو الإعمال أو التتبع، وغالبا ما ينظر إليها ذات طبيعة استشارية وأن قيمتها تبقى في حدود الجوانب المعنوية والأخلاقية.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وبخصوص حرية الفكر والمعتقد، يقول المندوب الوزاري المكلف بحقوق الإنسان بأنه لم يتبين بالمطلق، من خلال المعطيات النوعية المستخرجة من مختلف المصادر المعتمدة في هذا التقرير، تسجيل أي انتهاك خاص لحرية الفكر، حيث لم تسجل أي حالة اضطهاد حرية الفكر؛ وهو ما يؤكد أن ممارسة حرية الفكر تتم بحرية واسعة ودون أي قيود.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وعلى مستوى حرية التعبير، يقول بنيوب إنه &ldquo;يبقى مرتبطا بقضايا يتداخل فيها تدبير حرية الرأي والتعبير مع ضروريات حماية حريات وحقوق الآخرين وصيانة الأمن والنظام العام، ولا يرجع السبب في وقوعها إلى إرادة الدولة&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأورد أنه &ldquo;في ضوء الطابع السلمي للاحتجاجات من جهة المتظاهرين إلى حد الآن، رغم المحاولات المتكررة لاستغلالها من طرف البعض لاعتبارات سياسية، ومن جهة تدبير السلطات العمومية، الذي اتسم برابطة الجأش، أن السلوكين معا، ومع سائر الصعوبات والإكراهات، لا يزالان يرمزان إلى إرادات التشبث بالسلم المدني والعيش المشترك.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">واعتبر أن &ldquo;فتح أماكن الحرمان من الحرية بمختلف أصنافها، حيث يشتغل ضباط الشرطة القضائية ورجال الأمن وحراس السجون أمام المراقبة العمومية والمستقلة التي تمارسها الآلية الوطنية للوقاية من التعذيب، أهم ضمانة لتكريس شفافية أنظمة الحرمان من الحرية وجعلها قابلة للخضوع للمراقبة الخارجية والمستدامة&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وقال أحمد شوقي بنيوب بأن المندوبية الوزارية المكلفة بحقوق الإنسان ستقوم بإصدار تقرير موضوعاتي حول وضعية حقوق الإنسان بالأقاليم الجنوبية للمملكة في غضون الشهور الأولى للسنة المقبلة، كما سيتم إصدار تقرير حول الحكامة الأمنية وحقوق الإنسان في شهر ماي من السنة المقبلة.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393065316434323233302d7361686172612e77656270, '2021-12-14 21:35:25', '1', 'كشف أحمد شوقي بنيوب، المندوب الوزاري المكلف بحقوق الإنسان، عن مقاربة المملكة في تعاطيها مع المنظمات الحقوقية الدولية الناشطة في مجال حقوق الإنسان، مثل “مراسلون بلا حدود”، حيث ربط تعامل المغرب مع هذه المنظمات بطبيعة موقفها '),
(39, '\"النواب\" يمنع الإعلام من حضور أشغال اللجان', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">قرر مجلس النواب منع وسائل الإعلام من حضور جلسات اللجان الدائمة دون إخبار مسبق.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/commission_sante.jpg\" style=\"height:449px; width:674px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وفوجئت جريدة هسبريس صباح اليوم الثلاثاء 14 دجنبر الجاري بمنعها من ولوج مجلس النواب لتغطية اجتماع لجنة القطاعات الاجتماعية، بحضور وزير الصحة والحماية الاجتماعية وأعضاء اللجنة العلمية لتتبع جائحة كورونا؛ وذلك لدراسة &ldquo;مواكبة تطور الوضع الوبائي وخطة التصدي للمتحورات الجديدة من فيروس كورونا والتدابير والإجراءات التي اتخذتها الوزارة لمواجهتها&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">رجال الأمن المتواجدون بباب مقر مجلس النواب أخبروا هسبريس بتلقيهم مراسلة من المجلس يمنع بموجبها حضور الصحافيين لاجتماعات اللجان الدائمة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وفي اتصال مع عضو بمكتب مجلس النواب أكد صحة الخبر، لكنه وعد بالعمل على إعادة النظر فيه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وجاء هذا القرار بعد الجدل الذي أثير خلال اجتماع عقدته لجنة التعليم والثقافة والاتصال في 26 من نونبر الماضي، بحضور وزير التربية الوطنية والتعليم الأولي، شكيب بنموسى؛ وهي الجلسة التي عرفت احتجاج بعض النواب على عدم بثها في الصفحات الرسمية للمجلس على مواقع التواصل الاجتماعي، بينما قامت بعض المواقع ببثها، وهو المعطى الذي تأكدت عدم صحته، إذ تبين أن أحد برلمانيي الأغلبية هو الذي قام ببث مباشر على صفحته بموقع التواصل الاجتماعي &ldquo;فيسبوك&rdquo;، قبل أن يحذفه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وحسب المعطيات المتوفرة فإن مجلس النواب قرر كذلك التراجع عن بث جلسات اللجان الدائمة مباشرة على موقعه الرسمي، وهو ما يعتبر تراجعا عن حق المواطنين في تتبع ما يقوم به البرلمان.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393065353761323464312d636f6d6d697373696f6e5f73616e74652e77656270, '2021-12-14 21:36:23', '1', 'قرر مجلس النواب منع وسائل الإعلام من حضور جلسات اللجان الدائمة دون إخبار مسبق.'),
(40, 'سفارة روسيا تكشف سبب تأجيل زيارة لافروف', '<p dir=\"rtl\"><span style=\"font-size:18px\">كشفت السفارة الروسية بالرباط خلفيات تأجيل زيارة وزير الخارجية الروسي، سيرجي لافروف، التي كانت مقررة هذا الأسبوع إلى المغرب، لحضور أشغال منتدى التعاون الروسي العربي في مدينة مراكش.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/bourita-Serguei-Lavrov.jpg\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأوردت السفارة الروسية، في توضيح لجريدة هسبريس الإلكترونية، أن تأجيل زيارة لافروف إلى موعد لاحق تم بعد مشاورات مع المشاركين والأطراف المعنية لأسباب تتعلق أساسا بتطورات الوضعية الوبائية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وكان وزير الخارجية الروسي ألغى زيارته إلى المغرب والجزائر، وأيضا إلى منطقة الشرق الأوسط.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">يشار إلى أن موعد انعقاد المنتدى العربي الروسي الذي كان مقررا غدا الأربعاء في مدينة مراكش يتزامن مع قرار السلطات المغربية القاضي باستمرار تعليق الرحلات الجوية المباشرة للمسافرين، بسبب الانتشار السريع للمتحور الجديد لكوفيد-19 أوميكرون، خصوصا بأوروبا وإفريقيا.</span></p>\r\n', 0x6a616d6133612d363162393065396336336462322d626f75726974612d536572677565692d4c6176726f762e77656270, '2021-12-14 21:37:32', '1', 'كشفت السفارة الروسية بالرباط خلفيات تأجيل زيارة وزير الخارجية الروسي، سيرجي لافروف، التي كانت مقررة هذا الأسبوع إلى المغرب، لحضور أشغال منتدى التعاون الروسي العربي في مدينة مراكش.\r\n'),
(41, 'أخنوش يتتبع تنفيذ مخطط تهيئة جماعة أكادير', '<p style=\"text-align:right\"><span style=\"font-size:18px\">فقد عزيز أخنوش، رئيس المجلس الجماعي لمدينة أكادير، اليوم الثلاثاء، سير أشغال المشاريع الكبرى لتهيئة المجال الحضري، مرفوقا بأحمد حجي والي جهة سوس ماسة، وكريم أشنكلي، رئيس جهة سوس ماسة.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وأشرف رئيس المجلس الجماعي لأكادير تقديم المشروع السياحي &ldquo;دانيا لاند&rdquo;، حيث تفقد أولى عربات &ldquo;التلفريك&rdquo; التي ستربط جسر تيلدي بقصبة أكادير أوفلا، قبل الانتقال إلى غرفة الولاية لعقد اجتماع لمتابعة سير أشغال تجهيز المحور الطرقي الشرقي الغربي والمنطقة السياحية، والذي عرضهما بالتفصيل المهندسون المعماريون المسؤولون عن هذه المشاريع.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">كما قام عزيز أخنوش، بصفته رئيسا لمجلس جماعة أكادير، على مستوى منطقة تالبورجت، بزيارة موقع إعادة تأهيل سينما الصحراء؛ وهو مبنى رمزي يخضع حاليا لتهيئة كاملة ويندرج ضمن مشاريع التنمية الثقافية للمدينة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-916431\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir4.jpeg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir4-768x512.jpeg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/akhannouch-agadir4.jpeg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/akhannouch-agadir4.jpeg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/akhannouch-agadir4.jpeg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/akhannouch-agadir4.jpeg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/akhannouch-agadir4.jpeg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir4.jpeg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وبالحي نفسه دائما، تابع أخنوش تقدم أعمال تجهيز سوق&rdquo; تالبورجت&rdquo; الرامية إلى إعادة تأهيل هذه المنطقة التجارية من أجل التنمية الاجتماعية والاقتصادية للمنطقة، ثم توقف عند مشروع مدينة الفنون؛ وهو مبنى طال انتظاره من قبل ساكنة أكادير والذي سيبث حياة جديدة في المشهد الثقافي بالجهة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-916430\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir3.jpeg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir3-768x512.jpeg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/akhannouch-agadir3.jpeg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/akhannouch-agadir3.jpeg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/akhannouch-agadir3.jpeg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/akhannouch-agadir3.jpeg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/akhannouch-agadir3.jpeg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/akhannouch-agadir3.jpeg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">كما أشرف رئيس المجلس الجماعي لأكادير على افتتاح القاعة الرياضية المغطاة بـ&rdquo;الزرقطوني&rdquo; وتوزيع معدات رياضية في تخصصات كرة القدم وكرة السلة لصالح الشباب ذوي الاحتياجات الخاصة، حيث أشاد بإنجازات بعض الوجوه الرياضية في أكادير الذين يمثلون المغرب على الصعيد الوطني والدولي بالموازاة مع زيارة أشغال تهيئة الأحياء غير المجهزة بتكوين.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وتأتي هذه الزيارة في إطار متابعة تنفيذ مخطط تهيئة المجال الحضري لأكادير، وللوقوف على تنفيذ مختلف الأشغال بمجموعة من المشاريع التنموية، التي ستعزز الدور الاقتصادي والسياحي والثقافي لمدينة &ldquo;الانبعاث&rdquo;.</span></p>\r\n', 0x6a616d6133612d363162393066363163636139312d616b68616e6e6f7563682d6167616469722e77656270, '2021-12-14 21:40:49', '2', 'فقد عزيز أخنوش، رئيس المجلس الجماعي لمدينة أكادير، اليوم الثلاثاء، سير أشغال المشاريع الكبرى لتهيئة المجال الحضري، مرفوقا بأحمد حجي والي جهة سوس ماسة، وكريم أشنكلي، رئيس جهة سوس ماسة.'),
(42, 'التوزيع الجغرافي لإصابات \"كورونا\" في المغرب', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">أعلنت وزارة الصحة والحماية الاجتماعية، اليوم الثلاثاء، أنه تم تسجيل صفر حالة وفاة، و 219 إصابة بفيروس (كوفيد-19)، فيما بلغ عدد المتعافين 234 خلال ال24 ساعة الماضية.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/11/Coronavirus.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأبرزت الوزارة في النشرة اليومية لحصيلة (كوفيد-19)، أن مليونين و 99 ألف و 402 شخصا تلقوا الجرعة الثالثة من اللقاح المضاد للفيروس، فيما ارتفع عدد الملقحين بالجرعة الثانية إلى 22 مليون و 797 ألف و887 شخصا، مقابل 24 مليون و 475 ألف و639 تلقوا الجرعة الأولى.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأشارت النشرة إلى أن الحصيلة الجديدة للإصابات بالفيروس رفعت العدد الإجمالي لحالات الإصابة المؤكدة بالمملكة إلى 951 ألف و 763 حالة منذ الإعلان عن أول حالة في 2 مارس 2020، فيما بلغ مجموع حالات الشفاء التام 935 ألف و 592 حالة بنسبة تعاف تبلغ 98.3 في المائة، فيما استقر عدد الوفيات في حدود 14 ألف و798 بنسبة فتك تصل إلى 1.6 في المائة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وتتوزع حالات الإصابة المسجلة خلال الـ24 ساعة الأخيرة بين جهات الدار البيضاء-سطات (139) والرباط-سلا-القنيطرة (47) ومراكش-آسفي (14) و فاس مكناس (7) و طنجة-تطوان-الحسيمة (4) وبني ملال خنيفرة (2) وسوس ماسة (2) ودرعة تافيلالت (2) والشرق (2) .</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وبلغ مجموع الحالات النشطة 1388، فيما بلغ عدد الحالات الخطيرة أو الحرجة الجديدة حالتين خلال الـ24 ساعة الأخيرة، ليصل مجموع هذه الحالات إلى 100 حالة، منها حالتين تحت التنفس الاصطناعي الاختراقي.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وبلغ معدل ملء أسرة الإنعاش المخصصة لـ(كوفيد-19) 1.9 بالمائة.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393066656237636562372d436f726f6e6176697275732e77656270, '2021-12-14 21:43:07', '2', 'أعلنت وزارة الصحة والحماية الاجتماعية، اليوم الثلاثاء، أنه تم تسجيل صفر حالة وفاة، و 219 إصابة بفيروس (كوفيد-19)، فيما بلغ عدد المتعافين 234 خلال ال24 ساعة الماضية.'),
(43, 'طلاب ينظمون وقفة احتجاج في مدينة تزنيت', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">نظم طلبة الأقسام التحضيرية لنيل شهادة التقني العالي، الاثنين، وقفة احتجاج أمام الثانوية التأهيلية المسيرة الخضراء بتزنيت احتجاجا على ما أسموه &ldquo;سياسة الآذان الصماء التي تنهجها الوزارة الوصية على القطاع في التعامل مع ملفهم المطلبي&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وصدحت حناجر الطلبة المحتجين في شكلهم النضالي، الذي تم نقله نحو مقر المديرية الإقليمية للأكاديمية الجهوية للتربية والتكوين بتزنيت، شعارات من قبيل: &ldquo;هذا عار هذا عار الطالب في خطر&rdquo;، و&rdquo;الطلبة ها هوما والإجازة فينا هيا&rdquo;، وغيرها من الشعارات المنددة بضرورة التجاوب مع مطالبهم كفئة متضررة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-915925\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1-768x512.jpeg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit1.jpeg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وعلى رأس المطالب التي نادى بها طلبة الأقسام التحضيرية لنيل شهادة التقني العالي بتزنيت فتح المجال أمامهم من أجل ولوج مسلك الإجازة المهنية، وصرف المنحة الجامعية وتعميمها، وتمكين جميع الطلبة من بطاقة الطالب، فضلا عن توفير مكتبة مجهزة تليق بالشعبة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">كما تضمن الملف المطلبي، الذي توصلت به هسبريس، الاستفادة من الأحياء الداخلية والجامعية، وتأمين الطلبة خلال مجريات الدورات التكوينية، وفتح باب خاص بالطلبة لولوج المؤسسة اعتبارا لوجود الأقسام المخصصة للتقني العالي داخل الثانوية التأهيلية المسيرة الخضراء.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-915926\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2-768x512.jpeg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/sit-in-etudients-lycee-qualifiant-almassira-alkhadra-tiznit2.jpeg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وتفاعلا مع هذا الشكل الاحتجاجي استقبل المهدي الرحيوي، المدير الإقليمي للأكاديمية الجهوية للتربية والتكوين بتزنيت، ممثلين عن الطلبة المتضررين. وهمّ اللقاء مناقشة كافة النقاط الواردة في الملف المطلبي، قبل أن يخلص إلى الاتفاق على استئناف الطلبة لدروسهم التعليمية في انتظار إيجاد حلول للمشاكل المطروحة.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393130323733313437662d7369742d696e2d6574756469656e74732d6c796365652d7175616c696669616e742d616c6d6173736972612d616c6b68616472612d74697a6e69742e77656270, '2021-12-14 21:44:07', '2', 'نظم طلبة الأقسام التحضيرية لنيل شهادة التقني العالي، الاثنين، وقفة احتجاج أمام الثانوية التأهيلية المسيرة الخضراء بتزنيت احتجاجا على ما أسموه “سياسة الآذان الصماء التي تنهجها الوزارة الوصية على القطاع في التعامل مع ملفهم ا'),
(44, 'هزة أرضية بقوة 3.7 درجات في إقليم الدريوش', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">أعلن المعهد الوطني للجيوفيزياء عن تسجيل هزة أرضية بلغت قوتها 3,7 درجة على سلم &ldquo;ريشتر&rdquo;، اليوم الإثنين، بإقليم الدريوش.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/03/seisme-japon.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأوضحت الشبكة الوطنية للمراقبة والإنذار الزلزالي التابعة للمعهد، أن هذه الهزة، التي حدد مركزها بعرض ساحل إقليم الدريوش، وقعت على الساعة الثانية عشرة و 40 دقيقة و41 ثانية (توقيت غرينيتش+1).</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأضاف المصدر ذاته، أن الهزة سجلت على عمق 26 كيلومترا، عند التقاء خط العرض 35.594 درجة شمالا، وخط الطول 3.662 درجة غربا.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393130373234373230322d736569736d652d6a61706f6e2e77656270, '2021-12-14 21:45:22', '2', 'أعلن المعهد الوطني للجيوفيزياء عن تسجيل هزة أرضية بلغت قوتها 3,7 درجة على سلم “ريشتر”، اليوم الإثنين، بإقليم الدريوش.'),
(45, 'هذه تفاصيل وضعية الوباء في جهات المملكة‎‎', '<p dir=\"rtl\"><span style=\"font-size:18px\">أعلنت وزارة الصحة والحماية الاجتماعية، اليوم الاثنين، أنه تم تسجيل حالتي وفاة، و 62 إصابة جديدة بفيروس &ldquo;كوفيد-19&Prime;، فيما بلغ عدد المتعافين 291 خلال ال24 ساعة الماضية.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/coronavirus-sante.jpg\" style=\"height:412px; width:617px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأبرزت الوزارة في النشرة اليومية لحصيلة &ldquo;كوفيد-19&Prime;، أن مليونين و37 ألف و 885 شخصا تلقوا الجرعة الثالثة من اللقاح المضاد للفيروس، فيما ارتفع عدد الملقحين بالجرعة الثانية إلى 22 مليون و 786 ألف و591 شخصا، مقابل 24 مليون و470 ألف و391 تلقوا الجرعة الأولى.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأشارت النشرة إلى أن الحصيلة الجديدة للإصابات بالفيروس رفعت العدد الإجمالي لحالات الإصابة المؤكدة بالمملكة إلى 951 ألف و 544 حالة منذ الإعلان عن أول حالة في 2 مارس 2020، فيما بلغ مجموع حالات الشفاء التام 935 ألف و 358 حالة بنسبة تعاف تبلغ 98.3 في المائة، فيما استقر عدد الوفيات في حدود 14 ألف و798 بنسبة فتك تصل الى 1.6 في المائة.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وسجلت حالتي الوفاة في جهة الرباط-سلا-القنيطرة (2)، بينما تتوزع حالات الإصابة المسجلة خلال الـ24 ساعة الأخيرة بين جهة الرباط-سلا-القنيطرة (38) والدار البيضاء-سطات (15) ومراكش-آسفي (4) و طنجة-تطوان-الحسيمة (2) وجهة الشرق (2) وسوس ماسة (1) .</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وبلغ مجموع الحالات النشطة 1388، فيما بلغ عدد الحالات الخطيرة أو الحرجة الجديدة 8 حالات خلال الـ24 ساعة الأخيرة، ليصل مجموع هذه الحالات إلى 103 حالات، منها 4 حالات تحت التنفس الاصطناعي الاختراقي.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وبلغ معدل ملء أسرة الإنعاش المخصصة لـ&rdquo;كوفيد-19&rdquo; 2 بالمائة</span></p>\r\n', 0x6a616d6133612d363162393130616330646631662d636f726f6e6176697275732d73616e74652e77656270, '2021-12-14 21:46:20', '2', 'أعلنت وزارة الصحة والحماية الاجتماعية، اليوم الاثنين، أنه تم تسجيل حالتي وفاة، و 62 إصابة جديدة بفيروس “كوفيد-19″، فيما بلغ عدد المتعافين 291 خلال ال24 ساعة الماضية.'),
(46, 'جماعة شفشاون تبدأ سلسلة تكوينات الأعضاء', '<div class=\"article-content\">\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">في إطار التكوين المستمر لعضوات وأعضاء المجلس الجماعي لمدينة شفشاون، انطلقت فعاليات لقاء تكويني أشرف عليه الدكتور حميد أبولاس، أستاذ التعليم العالي بالكلية متعددة التخصصات بتطوان.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">اللقاء أعطى انطلاقته محمد السفياني، رئيس جماعة شفشاون، الذي أكد في كلمة بالمناسبة على الأدوار المهمة التي يقوم بها الأعضاء أغلبية ومعارضة من خلال وضع تصورات واقتراحات والمساهمة في تفعيل آليات الحكامة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter alignnone size-full wp-image-915905\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation1.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation1-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/commune-Chefchaouen-formation1.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/commune-Chefchaouen-formation1.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/commune-Chefchaouen-formation1.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/commune-Chefchaouen-formation1.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/commune-Chefchaouen-formation1.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation1.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وأضاف السفياني أن الجماعة ستنكب، في غضون الأسابيع القليلة المقبلة، على سلسلة من التكوينات للأعضاء تهمّ مختلف مجالات وقطاعات تدبير الجماعة.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وانصبت مداخلة الدكتور حميد أبولاس على شرح مقتضيات القانون التنظيمي رقم 113/14 المتعلق بالجماعات، حيث توقف على المقتضيات والمراسيم المتعلقة بذلك، في أفق إعطاء مفهوم جديد لتدبير الشأن العام المحلي.. فضلا عن مجموعة من المستجدات التي جاء بها القانون التنظيمي.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وحلل الأستاذ المحاضر المكانة التي يوليها الدستور للجماعات الترابية، متوقفا عند أبعاد ودلالات الهندسة الترابية في جانب المضامين والتطلعات التي أضفت عليها طابع الحداثة والتجديد والإبداع، خاصة على مستوى التدبير والنجاعة..</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-915906\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation2.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation2-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/commune-Chefchaouen-formation2.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/commune-Chefchaouen-formation2.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/commune-Chefchaouen-formation2.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/commune-Chefchaouen-formation2.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/commune-Chefchaouen-formation2.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/commune-Chefchaouen-formation2.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وألقى أستاذ التعليم العالي بالكلية متعددة التخصصات بتطوان الضوء على النظام المالي للجماعة واختصاصاتها وكيفية تنمية التعاون بين الجماعات، إلى غير ذلك من الأفكار والتطلعات المهمة التي تحتاج أثناء تجسيدها وتفعيلها إلى بعض الابتكار والاجتهاد.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">جدير بالذكر أن هذه الورشة التكوينية تميزت بنقاش مستفيض وتفاعلي بين عضوات وأعضاء المجلس الجماعي لمدينة شفشاون والأستاذ المؤطر.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393131326434653932302d636f6d6d756e652d436865666368616f75656e2d666f726d6174696f6e2e77656270, '2021-12-14 21:48:29', '2', 'في إطار التكوين المستمر لعضوات وأعضاء المجلس الجماعي لمدينة شفشاون، انطلقت فعاليات لقاء تكويني أشرف عليه الدكتور حميد أبولاس، أستاذ التعليم العالي بالكلية متعددة التخصصات بتطوان.'),
(47, 'نهج الحسن الثاني في شفشاون .. ذاكرة إنسانية تمضي بفصول الزمن', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">كانت شوارع مدينة شفشاون بشموسها البعيدة/القريبة، بجذورها العميقة الهاربة بدلالات الحياة والأحداث والصّور والشّخوص، جزءاً من سيرورة حضارية، تعيد إلينا ارتباطاً وجدانيّاً له أكثر من معنى، وتعيدنا إليها في كلّ لحظة ويوم ومناسبة لنمضي معاً إلى أن نفترق.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/hassan_2.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">في هذه النّظرة المتبقّية بالحنين، وبتلك الامتدادات الواسعة التي رافقت الانطلاقة من رحم الطّفولة إلى الآن، كان شارع نهج الحسن الثّاني (ساحة إسبانيا سابقاً)، المؤثّث بالأشجار الظّليلة، مفتوحاً بمسافات جمالية على أفق الجبال وباب العين والثكنة العسكريّة للاحتلال الإسباني سابقاً (ثانوية الإمام الشاذلي حاليا) ..وكذا ساحة &ldquo;روندة&rdquo;، والطّريق العابر صوب الفضاءات السّياحية بالمدينة العتيقة وغيرها.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">الشّارع في تمظهراته المختلفة كان ومازال الملاذ الرّئيسيّ لكلّ السّاكنة، خاصّة في المساء، كان بمثابة الشّريان الذي يتدفّق في وجداناتها وينقلها من حيّز الأحياء والتّجمّعات إلى محجّ منظّم يفضي إلى مدار ساحة محمد الخامس (الخاصة)، المُطلّة بالنّباتات والأغراس ومختلف الزّهور وبالممرّات الحالمة والوجوه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">بهذا المعنى وضمن هذه السّياقات يبقى نهج الحسن الثّاني ذاكرة أخرى حملت اسم الملك الرّاحل، إثر زيارته التّاريخية إلى مدينة شفشاون في بداية الستّينيات، إذ صار النّهج ومنذ ذلك الحين شاهداً على تجلّيات زيارة أرّخت لذكرى عزيزة وذات مغزى في قلوب الشّفشاونيّين، بعدما كان قد وقف الملك الراحل بهذا المكان ورفع السّتارة عن اللّوحة التذكارية الحاملة لاسمه.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وعرف النّهج ذاته، برحابته عبر سنوات طويلة، تسجيلا دقيقاً لمراحل وقصص من حياة كلّ واحدة وواحد من أبناء المدينة، وكذا تحوّلات زمانيّة؛ وكان جمّاع خبرات وعواطف ومناخات ومسارات وشخصيّات وأحداث وحكايات وتدافعات وحفلات وأعياد&hellip;ومنه تدفّقت الطّموحات والأحلام.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكنّا ونحن صغار مع أمّهاتنا في بعض المناسبات الوطنيّة نترقّب من على أسوار الشارع أسطول الشّاحنات المارّة، المزيّنة بالأعلام، وهي تجسّد باختصار جميل مهام القطاعات المختلفة بالمؤسّسات العمومية.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">هو أيضاً وبكلّ بساطة الشّارع الذي ينطوي على عديد الانكسارات ويختم جوازات أرواح بعض النّاس بمجاورته مقبرة وضريح مولاي علي بن راشد؛ فمنه تمرّ مواكب الجنائز في لحظات من الحزن البالغ، ومنه كذلك تمرّ السيّارات رافعة أبواقها بالصّخب وبنبضات العرسان. كما يضمّ الشارع المصالح الإدارية والمحلاّت التّجارية وبعض الآثار الكولونيالية؛ فضلا عن المكتبات التي تقدّم خدماتها اليوميّة لسكان المدينة والإقليم.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393131366662626265382d68617373616e5f322e6a7067, '2021-12-14 21:49:35', '2', 'بهذا المعنى وضمن هذه السّياقات يبقى نهج الحسن الثّاني ذاكرة أخرى حملت اسم الملك الرّاحل، إثر زيارته التّاريخية إلى مدينة شفشاون في بداية الستّينيات، إذ صار النّهج ومنذ ذلك الحين شاهداً على تجلّيات زيارة أرّخت لذكرى عزيزة و');
INSERT INTO `news` (`id`, `name`, `content`, `file`, `date`, `category_id`, `description`) VALUES
(48, 'غياب العدالة الاجتماعية والمجالية يعطل عجلة التنمية في جهة درعة تافيلات', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">تتمتع جهة درعة تافيلالت، المحدثة ضمن التقسيم الجهوي للمملكة المغربية لسنة 2015، بموقع جغرافي حدودي مهم على الضفة الشرقية للمغرب، وتحدها خمس جهات (بني ملال خنيفرة، فاس مكناس، مراكش، الشرق، سوس ماسة) مع شريط طويل من الحدود المغربية الجزائرية.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">تشير إحصائيات المندوبية السامية للتخطيط إلى أن سكان جهة درعة تافيلالت يمثلون حوالي 5 في المائة من سكان المملكة المغربية، حيث يبلغ عدد سكان الجهة (حسب إحصاء سنة 2014) ما مجموعه مليون و635 ألف نسمة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وبالإضافة إلى أن هذه الجهة تعد من الجهات الرائدة في المجال الفلاحي بالمملكة المغربية، خاصة في إنتاج التمور والزيتون والتفاح والورد العطري، فإنها تعتبر أيضا وجهة سينمائية وسياحية بامتياز. كما تضم أراضيها ثروات معدنية نفسية ومهمة، مثل الفضة والذهب والنحاس والحديد وغيرها من المعادن.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\">ثروة معدنية وفلاحية</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">تزخر درعة تافيلالت بأقاليمها الخمسة (تبلغ مساحتها 132 ألفا و167 كلم مربع) بمؤهلات للتنمية الاجتماعية والاقتصادية، خاصة في القطاعات الفلاحية والمعدنية والتجارية. وتساهم هذه الجهة في الاقتصاد الوطني بشكل كبير، وفق معطيات رسمية من المهتمين بالشأن العام بالجهة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter size-full wp-image-912623\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_1.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_1-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/tafilalat_1.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/tafilalat_1.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/tafilalat_1.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/tafilalat_1.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/tafilalat_1.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_1.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وتشير المعطيات نفسها إلى أن القطاعين الفلاح وقطاع المعادن يعتبران من أهم القطاعات المنتجة في الجهة، بالإضافة إلى التجارة وعائدات الجالية المقيمة بالخارج، مضيفة أنه رغم ذلك تعاني الجهة من ضعف تشغيل اليد العاملة؛ ما أدى إلى تزايد عدد العاطلين سنويا.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">الأرقام التي كشفت عنها وزارة الطاقة والمعادن سنة 2019 كشفت أن جهة درعة تافيلالت تنتج 702 ألف و811 طنا من المواد المعدنية، أي بحوالي 28 في المائة من الإنتاج الوطني عدا الفوسفاط.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ويبلغ إنتاج الشركات المعدنية بهذه الجهة من الفضة 220 طنا، والفليورين 5995 طنا، والحديد 3 آلاف طن، والمنغنيز 83078 طنا، والكوبالت 34910 أطنان، والزنك 13088 طنا، والنحاس 25532 طنا، والرصاص 559 طنا، والباريتين 63256 طنا، والطين 1070 طنا، والذهب 0,213 طنا.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وحسب المعطيات ذاتها، فيبلغ إنتاج النشاط المنجمي التقليدي بالجهة 472 470.102 طنا من معدن الباريتين، و573.778. 20 طنا من الزنك، و5.707.813 طنا من الرصاص، و450 طنا من خليط الرصاص والزنك، وينتج الاستغلال المنجمي التقليدي 88 في المائة من الإنتاج الإجمالي للجهة من مادة الباريتين.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter size-full wp-image-912620\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_5.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_5-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/tafilalat_5.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/tafilalat_5.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/tafilalat_5.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/tafilalat_5.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/tafilalat_5.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_5.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">في المقابل، سجل القطاع الفلاحي قفزة مهمة في الإنتاج الوطني، حيث بلغ إنتاج التمور أكثر من 128 ألف طن، و27 ألف طن من الزيتون، و400 ألف طن من التفاح؛ ما ساهم في ارتفاع نسبة مساهمة جهة درعة تافيلالت من الإنتاج الوطني الفلاحي بزيادة بلغت حوالي 10 في المائة مقارنة مع السنوات الأخيرة، وفق الأرقام الرسمية لمسؤولي الفلاحة.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\">شجرة الفقر تخفي غابة الغنى</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">على الرغم من كل ما تزخر به درعة تافيلالت من مؤهلات للتنمية الاجتماعية والاقتصادية، فإنها لا تزال تعاني من الفوارق الاجتماعية ومن غياب العدالة الاجتماعية والمجالية، وفق تصريح لسعيد أوميمون، فاعل حقوقي من إقليم ميدلت، مؤكدا أن التنمية في جميع المجالات لن تتغير في ظل ما تشهده من تزايد في الفوارق الاجتماعية الحالية وغياب العدالة الاجتماعية والمجالية بصفة عامة، وفق تعبيره.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وكشف الحقوقي ذاته أن نسبة الساكنة التي تعاني من الحرمان الشديد بالجهة تبلغ حوالي 48.2 في المائة، موضحا أن النسبة سالفة الذكر معرضة للفقر متعدد الأبعاد، خاصة القاطنين بالمناطق الجبلية والقروية، مؤكدا أن الثروات الباطنية من المعادن والفلاحة بإمكانها تخفيض نسبة الفقر إلى أقل من 5 في المائة.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter size-full wp-image-912621\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_3.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_3-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/tafilalat_3.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/tafilalat_3.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/tafilalat_3.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/tafilalat_3.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/tafilalat_3.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_3.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأوضح أوميمون أن هذه الجهة تعتبر من أغنى جهات المملكة؛ لكنها فقيرة بسبب سياسية التدبير المتبعة من قبل المجالس المنتخبة والمجلس الجهوي، مشددا على أن تنزيل الجهوية الموسعة أصبح أمرا ضروريا من أجل بناء الجهات من ثرواتها سواء المعدنية أو الفلاحية والتجارة وعائدات المهاجرين.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">في المقابل، أكد مصدر مسؤول بجماعة الرشيدية أن أقاليم هذه الجهة عانت ولا تزال تعاني من الركود التنموي، مشيرا إلى أن الأنظار تتجه إلى المجالس المنتخبة الجديدة خاصة مجلس الجهة من أجل تحريك عجلة التنمية؛ وذلك من أجل إعطاء هذه الجهة، التي تعتبر مهد الدولة العلوية، المكانة التي تستحقها وتخصيص ما تزخر به من مؤهلات لتنميتها أولا قبل تنمية المدن الأخرى على حساب أموال درعة تافيلالت.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأضاف المسؤول ذاته، في تصريح هاتفي لهسبريس، أن شجرة الفقر تخفي غابة الغنى والتطور الذي تشهده الجهة نتيجة ثرواتها الباطنية، موضحا أن الوقت حان لتنزيل الجهوية الموسعة بمفهومها الصحيح ومن حق كل منطقة الاستفادة من ثرواتها ومؤهلاتها أولا.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\">صراعات وبلوكاج تنموي</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">أجمع عدد من المهتمين بالشأن العام بجهة درعة تافيلالت أن هذه الأخيرة عانت وتعاني &ldquo;الفقر التنموي&rdquo; لسنوات عديدة، بسبب البرامج التنموية &ldquo;الفاشلة&rdquo; التي يتم اعتمادها، مشددين على أن هذه &ldquo;السياسة التنموية الفاشلة&rdquo; ساهمت في تردي واقع العديد من القطاعات الحيوية.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter size-full wp-image-912624\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_4.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_4-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/tafilalat_4.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/tafilalat_4.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/tafilalat_4.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/tafilalat_4.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/tafilalat_4.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_4.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وفي هذا الإطار، قال حسن عليلوش، فاعل مدني من إقليم تنغير، إن الصراعات السياسية التي عرفتها جهة درعة تافيلالت خلال الولاية السابقة لمجلس الجهة تسببت في تعثر العديد من البرامج التنموية وفشلها في تحقيق أهدافها، موضحا أن الصراعات السياسية سالفة الذكر أدت في نهاية المطاف إلى بلوكاج تنموي خطير عطل مصالح المواطنين عامة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأوضح المتحدث ذاته، في تصريح لهسبريس، أن الولاية السابقة غير مقنعة والساكنة عاقبت الواقفين وراء التعثر التنموي في الجهة، مشيرا إلى أن هناك آملا في عدم تكرار التجربة السابقة الفاشلة في تنزيل البرامج والمشاريع التنموية ذات البعدين الاجتماعي والاقتصادي بالجهة.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأشار الفاعل المدني نفسه إلى أن المواطن بجهة درعة تافيلالت ينتظر بلوغ المنتخبين مرحلة النضج والتفكير في المصلحة العامة لتحقيق إقلاع تنموي لهذه الجهة التي تتوفر على مليارات الدراهم من ميزانيتها، متمنيا عدم دخول المجلس الجهوي الجديد في صراعات سياسية قصد إخراج هذه الجهة من عنق الزجاجة بتجسيد برامج ومشاريع تنموية على أرض الواقع، وفق تعبيره.</span></p>\r\n\r\n<h2><span style=\"font-size:18px\">الأخطاء السابقة</span></h2>\r\n\r\n<p><span style=\"font-size:18px\">منذ سنة 2015، تاريخ إحداث جهة درعة تافيلالت وفق التقسيم الجهوي الأخير للمملكة المغربية، عرفت هذه الجهة تسييرا من طرف مجموعة من الأحزاب السياسية وبقيادة العدالة والتنمية في شخص الحبيب الشوباني، إلا أن البلوكاج كان عنوانا عريضا لهذه الولاية بسبب صراعات سياسية؛ الأمر الذي أدى إلى توقف التنمية بعموم أقاليم الجهة دون استثناء.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter size-full wp-image-912622\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_2.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_2-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/tafilalat_2.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/tafilalat_2.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/tafilalat_2.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/tafilalat_2.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/tafilalat_2.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/tafilalat_2.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأدت مجموعة من الأخطاء المرتكبة من قبل أغلبية المجلس السابق والمعارضة إلى بلوكاج تنموي ساهم فيه أشخاص همهم عرقلة سير قافلة التنمية بهدف الإطاحة بالغريم في الانتخابات الأخيرة، وساهم فيه أيضا أشخاص يقومون بتسيير أمور الجهة بدون دراسات ومخططات وبرامج عمل، وفق إفادة بعض الموظفين بإدارة المجلس الجهوي.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">ومن أجل نيل تعليق حول ما إن كان المكتب المسير الجديد للمجلس سيستفيد من الأخطاء السابقة لإعلان انطلاقة جديدة، اتصلت هسبريس بهرو أبرو، الرئيس الجديد لمجلس جهة درعة تافيلالت؛ إلا أنه لا يجيب في الهاتف بالرغم من توصله برسالة نصية، عكس ما كان سلفه يقوم به من خلال التواصل مع وسائل الإعلام.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393131663037366534642d746166696c616c61745f312e6a7067, '2021-12-14 21:51:44', '2', 'تتمتع جهة درعة تافيلالت، المحدثة ضمن التقسيم الجهوي للمملكة المغربية لسنة 2015، بموقع جغرافي حدودي مهم على الضفة الشرقية للمغرب، وتحدها خمس جهات (بني ملال خنيفرة، فاس مكناس، مراكش، الشرق، سوس ماسة) مع شريط طويل من الحدود ا'),
(49, 'دليل يقرب المهاجرين المسجونين بالمملكة من الحقوق والتشريعات المغربية', '<p dir=\"rtl\"><span style=\"font-size:18px\">&ldquo;الحرمان من الحرية لا يعني مطلقا الحرمان من الحقوق&rdquo; شعار يحمله دليل جديد للمرصد المغربي للسجون، يصدر في إطار سعي إلى ضمان حقوق السجناء الأجانب بالمغرب.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">&ldquo;دليل السجناء والسجينات الأجانب بالمغرب&rdquo; قدمه مرصد السجون باللغات الفرنسية والعربية والإنجليزية، اليوم الثلاثاء، بأحد فنادق العاصمة الرباط، بحضور ممثل عن المندوبية العامة لإدارة السجون وإعادة الإدماج. ومن المرتقب أن يطبع الدليل، أيضا، باللغة الإسبانية.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter alignnone size-full wp-image-916356\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc5.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويقصد هذا الدليل إعلامَ وتحسيس السجناء الأجانب بواقعهم السجني بالمغرب وبحقوقهم في البلاد، مع &ldquo;تقديم إرشادات عملية للسجناء والسجينات الأجانب بالمغرب، بشأن الإجراءات الواجب اتباعها من أجل التمتع بحقوقهم الأساسية&rdquo;، وتيسير تواصلهم مع &ldquo;محيطهم السجني، والعالم الخارجي، وضمان وصولهم إلى المعلومات&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">كما يحدد الدليل مواردَ لازمة من أجل &ldquo;مصاحبة أفضل ودعم ملائم للسجناء والسجينات الأجانب&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-916355\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc1-1.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويستهدف هذا الدليل &ldquo;كل مواطن من بلد أجنبي، لا يحمل الجنسية المغربية، تم اتهامه أو تمت إدانته، واتخذ في حقه تدبير سالب للحرية وتم إيداعه داخل مؤسسة سجنية بالمغرب&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويعدد المنشور الجديد الحقوق غير القابلة للتقييد، مثل الحق في الحياة والحرية والسلامة الشخصية، وعدم التعذيب ولا التعرض للمعاملة القاسية أو اللاإنسانية أو الحاطة بالكرامة، والحق في معاملة السجناء &ldquo;معاملة يكون هدفها الأساسي إصلاحهم وإعادة تأهيلهم الاجتماعي&rdquo;، إضافة إلى حقوق مدسترة ومقنّنة مثل &ldquo;الاعتقال في ظروف ملائمة للصحة والسلامة&rdquo;، وإمكان استفادة كل شخص سجين من &ldquo;برامج للتكوين وإعادة الإدماج&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويوضح الدليل للمسجونين من جنسيات غير مغربية ما يُلزم إدارة السجن تجاههم، والمخاطَبين الذين يمكنهم طلب التواصل معهم، والعقوبات التأديبية المترتبة عن عدم امتثال المسؤولين لهذه الواجبات، وعدم احترام حقوق السجناء.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويبين الدليل طريقة عمل السجون، والعقوبات التأديبية داخلها، وكيفية توزيع المساجين، وما يتيحه لهم شق &ldquo;إعادة الإدماج&rdquo; من أنشطة تعليمية وتكوينية ودينية وثقافية وترفيهية خلف القضبان. كما يعرج على كيفية سير الاستشفاء داخل السجن وخارجه، والحقوق المرتبطة به، وإجراءات الإضرابات عن الطعام، والعناية بالنظافة والتغذية واللباس والمتعلقات الشخصية والزيارات ورخص الخروج والمراسلات والمكالمات الهاتفية وكتابة الشكايات والتظلمات والولوج إلى العدالة والتسليم إلى سلطات خارج المغرب.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-916358\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc4.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ويضم الدليل نماذج يمكن اتباعها في صياغة الرسائل، لطلب العفو أو التنقيل أو للتبليغ عن خروقات أو تمييز داخل السجن. كما يورد عناوين ومعلومات التواصل مع جمعيات مشتغلة في المجال ومنظمات حقوقية مغربية، ومع مؤسسات وإدارات عمومية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وحول طريقة تعميم هذا الدليل ليصلَ المهاجرين المسجونين بالمغرب، قال عبد اللطيف الرفوع، رئيس المرصد المغربي للسجون، إن نسخا منه قد قدمت إلى البعثات الدبلوماسية بالبلاد.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأضاف الرفوع في تصريح لجريدة هسبريس الإلكترونية: &ldquo;&lsquo;مكّنّا السجناء الموجودين داخل المؤسسات السجنية من هذا الدليل، وسنضع رهن إشارة المندوبية العامة لإدارة السجون نسخا عديدة&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"alignnone size-full wp-image-916353\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc6.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وتابع المصرح شارحا: &ldquo;لقاء اليوم (14 دجنبر 2021) كان مع البعثات الدبلوماسية التي نراهن عليها في التواصل مع هؤلاء السجناء. والمندوبيةُ العامة في حد ذاتها لديها دليل بالعربية والفرنسية والإنجليزية&rdquo;؛ وهو ما وصفه بـ&rdquo;تضافر الجهود من أجل تقديم خدمة حقوقية لكل الفئات الهشة داخل المؤسسات السجنية، ومن يوجدون داخلها&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وذكر رئيس المرصد المغربي للسجون أن &ldquo;أزيد من 1100 سجين، خاصة من جنوب الصحراء وأمريكا اللاتينية وأوروبا، يوجدون بالسجون المغربية، ويتعرضون لمشاكل عدم التواصل اللغوي مع السجناء المغاربة، وعدم معرفتهم الحقوق التي يوفرها لهم التشريع المغربي الذي تلاءم مع الاتفاقيات الدولية&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" class=\"aligncenter alignnone size-full wp-image-916354\" loading=\"lazy\" sizes=\"( max-width : 100px ) 100px ,( max-width : 200px ) 200px ,( max-width : 300px ) 300px ,( max-width : 400px ) 400px ,( max-width : 500px ) 500px ,800px\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg\" srcset=\"https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7-768x512.jpg 768w, https://i1.hespress.com/wp-content/uploads/resize/100/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 100w, https://i1.hespress.com/wp-content/uploads/resize/200/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 200w, https://i1.hespress.com/wp-content/uploads/resize/300/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 300w, https://i1.hespress.com/wp-content/uploads/resize/400/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 400w, https://i1.hespress.com/wp-content/uploads/resize/500/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 500w, https://i1.hespress.com/wp-content/uploads/2021/12/Presentation-a-Rabat-du-guide-des-detenus-etrangers-au-Maroc7.jpg 800w\" style=\"height:533px; width:800px\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وزاد الرفوع موضحا أن &ldquo;دليل السجناء والسجينات الأجانب بالمغرب&rdquo; قد أعد &ldquo;بعد بحث ميداني أجريناه بمساعدة من المندوبية العامة لإدارة السجون وإعادة الإدماج، حيث قدّمت شكايات عديدة من المهاجرين السجناء؛ من قبيل عدم تواصل بعثاتهم الدبلوماسية معهم، وعدم معرفة عائلاتهم بوضعهم في المغرب&rdquo;</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/nAUOmG-MCes\" width=\"640\"></iframe></span></p>\r\n', 0x6a616d6133612d363162393133316161323266362d50726573656e746174696f6e2d612d52616261742d64752d67756964652d6465732d646574656e75732d657472616e676572732d61752d4d61726f632e77656270, '2021-12-14 21:56:42', '3', '“الحرمان من الحرية لا يعني مطلقا الحرمان من الحقوق” شعار يحمله دليل جديد للمرصد المغربي للسجون، يصدر في إطار سعي إلى ضمان حقوق السجناء الأجانب بالمغرب.'),
(50, 'بنموسى يدعو التلاميذ إلى الالتزام بالتدابير الوقائية وتلقي جرعات التلقيح', '<div class=\"article-content\" dir=\"rtl\">\r\n<p><span style=\"font-size:18px\">في خضم تراجُع عدد المواطنين المُقبلين على التلقيح ضد فيروس كورونا، دعا شكيب بنموسى، وزير التربية الوطنية والتعليم الأولي والرياضة، التلاميذ إلى مواصلة الانخراط في هذه العملية.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وقال بنموسى، في جلسة الأسئلة الشفوية بمجلس المستشارين، اليوم الثلاثاء، إن الوزارة &ldquo;تدعو التلاميذ إلى الالتزام بالتدابير الوقائية ومواصلة الاستفادة من جرعات التلقيح&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/09/vaccination-pour-la-categorie-d-age-12-17.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأضاف الوزير الوصي على قطاع التربية الوطنية أن الوزارة اتخذت تدابير تسهيل عملية تلقيح التلاميذ ضد فيروس كورونا، &ldquo;لتفادي أي انتكاسة وبائية لا قدر الله، علما أن العديد من الدول تواجه موجة وبائية خطيرة&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">من جهة ثانية، دافع بنموسى عن القرارات التي اتخذتها الوزارة في ما يتعلق بالموارد البشرية، لافتا إلى أن الهدف المتوخى هو التوفر على &ldquo;أطر ذات مؤهلات وقدرة على تنزيل أوراش الإصلاح وتجويد التعليم&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأردف أن الوزارة تولي أهمية كبرى لتطوير التكوين الأساسي للأطر التربوية، عبر تعميم مسالك الإجازة في التربية، وتحسين التكوين في المراكز الجهوية للتربية والتكوين، ووضع آليات لمواكبة التكوين المستمر لفائدة الأساتذة الجدد والممارسين، مشيرا إلى أن الميزانية المرصودة للتكوين المستمر تناهز 500 مليون درهم.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">واعتبر بنموسى أن &ldquo;المنجزات التي تم تحقيقها على مستوى توسيع قاعدة التعليم جعل الأنظار تصبو نحو مدرسةِ تكافؤ الفرص وتحسين جودة التعليم التي لا ترقى إلى المستوى المأمول&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وأضاف أن الحكومة تسعى إلى بلوغ هدف الجودة، من خلال تحسين مؤشريْ تقليص الهدر المدرسي والتمكّن من التعلمات والكفايات الأساس، مبرزا أن هذه الأهداف &ldquo;لن تتحقق إلا بمقاربة تشاركية وتكريس منهجية القرب والحكامة الترابية في إطار اللاتمركز&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">بنموسى دعا الفاعلين التربويين وعموم الشركاء المعنيين بالمدرسة إلى &ldquo;تكثيف جهود الإصلاح لتجديد ثقة الأسر في المدرسة ولتصير قاطرة نحو تحقيق التنمية&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">وعاد المسؤول الحكومي ذاته للتأكيد على أن وضعية المدرسة المغربية &ldquo;غير مُرضية&rdquo;، لافتا إلى أن النتائج التي توصل إليها المجلس الأعلى للتربية والتكوين والبحث العلمي حول مستوى التلاميذ &ldquo;تؤكد أن الإصلاح ضروري ومستعجل&rdquo;.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393133363831316536362d76616363696e6174696f6e2d706f75722d6c612d63617465676f7269652d642d6167652d31322d31372e77656270, '2021-12-14 21:58:00', '3', 'في خضم تراجُع عدد المواطنين المُقبلين على التلقيح ضد فيروس كورونا، دعا شكيب بنموسى، وزير التربية الوطنية والتعليم الأولي والرياضة، التلاميذ إلى مواصلة الانخراط في هذه العملية.'),
(51, 'حوار وزارة التربية ونقابات التعليم ينتهي بجلسة سادسة و\"لقاء المتعاقدين\"', '<p dir=\"rtl\"><span style=\"font-size:18px\">اتفقت النقابات التعليمية ووزارة التربية الوطنية والتعليم الأولي والرياضة على عقد جلسة حوار اجتماعي سادسة خلال نهاية شهر دجنبر الجاري، وذلك عقب اللقاء الذي جمع الطرفين اليوم الثلاثاء.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ووفقا لمصادر هسبريس فقد برمجت جلسة أخرى خاصة مع &ldquo;الأساتذة المتعاقدين&rdquo;، لكن دون تحديد التوقيت بالضبط، مشيرة إلى أن لقاء اليوم تداول في 7 ملفات تهم شغيلة التعليم.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><img alt=\"\" src=\"https://i1.hespress.com/wp-content/uploads/2021/12/Benmoussa-syndicats-de-l-enseignement.jpg\" /></span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأشارت مصادرنا إلى أن ملفات الإدارة التربوية وحاملي الشهادات والتوجيه والتخطيط التربوي والمساعدين التقنيين والإداريين والمكلفين خارج السلك طلبت الوزارة بشأنها وقتا إضافيا للدراسة.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">واعتبرت مصادر الجريدة أنه &ldquo;لا وجود إلى حدود اللحظة لحلول صادرة عن وزارة التربية الوطنية&rdquo;، مؤكدة أن &ldquo;النقابات تقترح إصدار مراسيم تعديلية لتسريع طرح الحلول للمشاكل العالقة&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وعن القانون الأساسي لوزارة التربية الوطنية، قالت مصادر هسبريس إن الاتفاق جرى على &ldquo;التداول بشأن مختلف نقاطه شهر يناير من السنة المقبلة&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وتطرح النقابات التعليمية 23 ملفا عالقا تهم شغيلة القطاع، يتقدمها ملف &ldquo;الأساتذة المتعاقدين&rdquo;، الذين دشنوا السنة الدراسية الجديدة بإضرابات واحتجاجات.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وتخوض تنسيقية الأساتذة المتعاقدين، منذ ما يقارب أربع سنوات، احتجاجات قوية تطالب من خلالها بالإدماج ضمن الوظيفة العمومية؛ لكن الوزارة المعنية تصر على أن نظام العقدة هو &ldquo;خيار دولة&rdquo; لا محيد عنه، وجاء من أجل تجويد المنظومة التعليمية.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ورغم سلسلة الحوارات الماراطونية بين المتنازعين فإن الوضع مازال ثابتا بين الوزارة و&rdquo;المتعاقدين&rdquo;؛ فيما يظل الهاجس الأكبر للمسؤولين هو استمرار التحاق الأفواج الجديدة بالاحتجاجات، ما يصعب مأمورية حل الملف ويوسع دائرة الرافضين.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وكان رئيس الحكومة، عزيز أخنوش، التزم بفتح حوار اجتماعي، خاصة مع المركزيات النقابية للتعليم الأكثر تمثيلية، من أجل التوافق حول الإجراءات والتدابير الرامية إلى الرفع التدريجي من الحد الأدنى للأجرة الصافية الشهرية عند بداية المسار المهني، لحملة شهادة التأهيل التربوي من المراكز الجهوية لمهن التربية والتكوين</span></p>\r\n', 0x6a616d6133612d363162393133653462643261312d42656e6d6f757373612d73796e6469636174732d64652d6c2d656e736569676e656d656e742e77656270, '2021-12-14 22:00:04', '3', 'اتفقت النقابات التعليمية ووزارة التربية الوطنية والتعليم الأولي والرياضة على عقد جلسة حوار اجتماعي سادسة خلال نهاية شهر دجنبر الجاري، وذلك عقب اللقاء الذي جمع الطرفين اليوم الثلاثاء.'),
(52, 'المغرب يسجل 219 إصابة دون وفيات بفيروس \"كورونا\" في 24 ساعة', '<div class=\"article-content\">\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">كشفت وزارة الصحة والحماية الاجتماعية، اليوم الثلاثاء، تسجيل 219 إصابة مؤكدة جديدة بفيروس &ldquo;كورونا&rdquo; خلال الـ24 ساعة الماضية، ليرتفع العدد الإجمالي للمصابين بالفيروس إلى 951763 حالة في المغرب.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ووفق النشرة اليومية للوضعية الوبائية فإن مجموع التحاليل المنجزة، عقب إجراء 11067 من الفحوصات الجديدة، قد بلغ 9631317 منذ بداية انتشار الفيروس على المستوى الوطني؛ في 2 مارس من العام الماضي.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وأفادت المعطيات الرسمية بأن الفترة نفسها لم تسجل أي وفاة بالفيروس ليستقر العدد الإجمالي في 14798.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وتم التأكد أيضا، وفق المصدر ذاته، من 234 حالة شفاء إضافية ليصل التعافي إلى 935592.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393134323531666362652d636f7669642d72616261742e77656270, '2021-12-14 22:01:09', '3', 'كشفت وزارة الصحة والحماية الاجتماعية، اليوم الثلاثاء، تسجيل 219 إصابة مؤكدة جديدة بفيروس “كورونا” خلال الـ24 ساعة الماضية، ليرتفع العدد الإجمالي للمصابين بالفيروس إلى 951763 حالة في المغرب.'),
(53, 'دراسة ميدانية ترصد هشاشة أوضاع \"خادمات البيوت\" في المغرب', '<p dir=\"rtl\"><span style=\"font-size:18px\">خلصت دراسة ميدانية إلى أن العمالة المنزلية، التي غالباً ما تزاولها النساء في المغرب، تتميز بأجور منخفضة، وعدم احترام تشريعات العمل التي اعتبرتها أصلا غير كافية، وحماية اجتماعية محدودة، مشيرة إلى أن المجال &ldquo;قطاع مبخّس القيمة&rdquo; وغير مهيكل في غالبيته القصوى بالبلد.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وأكدت الدراسة، المعنونة بـ&rdquo;مشروع تعزيز العمل اللائق في المغرب من خلال حماية وتنظيم العمالة المنزلية&rdquo;، أن المغرب لا يتوفر على معطيات رسمية دقيقة حول العمالة المنزلية، لكن بعض التقديرات الرسمية تشير إلى 200 ألف عامل وعاملة منزلية، بينما تقدّر مؤسسات نقابية ومدنية العدد بمليون شخص.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وحسب مخرجات الدراسة، التي أعدها مركز الدراسات والأبحاث في العلوم الاجتماعية بشراكة مع الكونفدرالية الديمقراطية للشغل، فإن النساء يشكّلن تسعين في المائة من العمالة المنزلية بالمغرب، مبرزة أن عدد عقد الشغل لا تتجاوز 2000 رغم صدور قانون العمالة المنزلية سنة 2016.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">ولفت المسح الميداني، الذي شمل مدن الدار البيضاء والرباط وطنجة، إلى أن عدد الأسر بالمغرب يتجاوز 8.5 ملايين أسرة، وبالتالي فإن اللجوء إلى العمالة المنزلية ليس محصوراً على الأسر الغنية أو الميسورة، بل يشمل تقريبا كل الأسر رغم اختلاف موقعها السوسيو-اقتصادي.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وترى الوثيقة أن &ldquo;القانون المغربي رقم 19-12 بشأن عاملات وعمال المنازل دخل حيز التنفيذ في أكتوبر 2018، وكان من المفروض أن يمنح مئات الآلاف من عاملات وعمال المنازل الحماية الاجتماعية التي حرموا منها لفترة طويلة، لكن يتضح أن تطبيقه بعيد جدا عن الأهداف المتوقعة&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وتابعت الدراسة بأن &ldquo;إحصائيات الصندوق الوطني للضمان الاجتماعي تشير إلى التصريح بـ 4523 عاملة وعاملا منزليا فقط في الضمان الاجتماعي إلى نهاية غشت 2021&Prime;، داعية إلى التعداد الدقيق للعمالة المنزلية وقياسها الوظيفي والإحصائي، ودمجها في نظام المحاسبة الوطنية، وتعزيز الاعتراف بها من قبل الدولة والمجتمع.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">كما دعمّت الوثيقة فكرة العمل المشترك والمتكامل والمندمج من أجل تعزيز العمل اللائق للعاملات والعمال المنزليين، بما يشمل السلطة التنفيذية (وزارة الإدماج الاقتصادي ووزارة المالية ووزارة الصحة ووزارة التضامن ووزارة الشباب)، والسلطة التشريعية لإصلاح القانون (الغرفتان)، والسلطة القضائية (النيابة العامة)، ونقابات العمال وأرباب العمل، وجمعيات المجتمع المدني.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وركزت حملة التنسيب والتنظيم النقابي للعمالة المنزلية على المطالبات الثماني التالية؛ وهي عقدة عمل مكتوبة قانونية، وأجر لائق للحياة الكريمة، والتصريح في الضمان الاجتماعي، ومدة عمل قانونية، وصحة وسلامة مهنية، والحق في الراحة والعطل والرخص والإجازة، ومنع العمل الجبري والإلزامي وتشغيل الأطفال، وحق التعبير والتنظيم النقابي، وضمان حقوق مماثلة للعمالة المنزلية المهاجرة بالمغرب.</span></p>\r\n\r\n<p dir=\"rtl\"><span style=\"font-size:18px\">وبخصوص تأثير الجائحة على العمالة المنزلية بالمغرب، اعتبرت الوثيقة أن مجموعة كبيرة من العاملات وجدن أنفسهن بدون عمل وبدون أجر، إذ استغنت مجموعة من الأسر عن العاملة المنزلية خوفا من العدوى، ولم تستفد شريحة كبيرة من العاملات من المساعدة الصحية بسبب عدم توفرهن على &ldquo;بطاقة راميد&rdquo;.</span></p>\r\n\r\n<p dir=\"rtl\" style=\"text-align:center\"><span style=\"font-size:18px\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/arzBNKU_YHQ\" width=\"640\"></iframe></span></p>\r\n', 0x6a616d6133612d363162393134383132663765642d626f6e6e655f646f6d657374697175655f312e77656270, '2021-12-14 22:02:41', '3', 'خلصت دراسة ميدانية إلى أن العمالة المنزلية، التي غالباً ما تزاولها النساء في المغرب، تتميز بأجور منخفضة، وعدم احترام تشريعات العمل التي اعتبرتها أصلا غير كافية، وحماية اجتماعية محدودة، مشيرة إلى أن المجال “قطاع مبخّس القيم'),
(54, 'شركات الطيران تستأنف الإقلاع من المغرب.. وعودة العالقين تبدأ بتركيا', '<div class=\"article-content\">\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">شرعت طائرات الخطوط الملكية المغربية في الاستعداد لتنظيم رحلات السفر صوب بلدان العالم، بعد أسابيع عطالة قسرية فرضتها سياقات جائحة كوفيد19 ومتحور أوميكرون.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ووفق مصدر من داخل الخطوط الملكية فالطائرات المغربية انطلقت فعليا نحو وجهات فرنسا وإيطاليا وإسبانيا والسنغال، معتبرا أن الذاهبين لن يعاملوا بمنطق &ldquo;العالقين&rdquo; عند طلبهم العودة إلى المغرب.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وأضاف مصدر هسبريس أن &ldquo;العملية انطلقت فعلا، لكن دون أحقية العودة مع العالقين&rdquo;، وزاد: &ldquo;أي مواطن مغربي من حقه الآن الخروج صوب الوجهة المرغوبة، لكن العودة ليست على مسؤولية السلطات&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وأوضح مصدرنا بخصوص العالقين أن الاستعدادات قائمة وستنطلق ابتداء من يوم غد الأربعاء، برحلة تقل العالقين من تركيا، مشيرا إلى أن هذه العملية ليست سهلة وتتطلب تنسيقا واسعا.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وحسب مصادر هسبريس فالتنسيق جار مع بلدان العالقين، وكذلك إدارات المطارات لتسهيل العملية، باستصدار التصريحات المتعلقة بتنظيم رحلات إجلاء العالقين.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وكانت اللجنة بين الوزارية للتنسيق وتتبع جائحة &ldquo;كوفيد-19&rdquo; أعلنت أن السلطات المغربية تسمح، بصفة استثنائية، بالرحلات الجوية للركاب نحو المملكة انطلاقا من البرتغال وتركيا والإمارات العربية المتحدة.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">هذا القرار، يوضح بلاغ اللجنة، يأتي من أجل تمكين المواطنين المغاربة المقيمين فعلا بالمغرب من العودة إلى المملكة، والذين ظلوا عالقين إثر توقف الرحلات الجوية.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ووفق المصدر فإن هذه العملية التي ستنطلق ابتداء من يوم الأربعاء 15 دجنبر تهم حصريا المواطنين المغاربة المقيمين فعلا بالمغرب، والذين غادروا التراب الوطني مؤخرا.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">بلاغ اللجنة بين الوزارية شدد على أن المستفيدين من هذه العملية سيتحملون تكاليف السفر.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وسيطبق البروتوكول الصحي على النحو التالي:</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ـ يتعين على كل مسافر تقديم نتيجة اختبار الكشف &ldquo;بي سي إر&rdquo; قبل السفر بـ48 ساعة على الأقل..</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ـ يخضع المسافرون للحجر الصحي طيلة سبعة أيام في فنادق مخصصة لهذا الغرض على نفقة الحكومة المغربية..</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">ـ سيتم إجراء اختبارات كشف &ldquo;بي سي إر&rdquo; خلال فترة الحجر الصحي كل 48 ساعة..</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:18px\">وسيجري التكفل بحالة كل من جاءت نتيجة كشفه إيجابية، سواء بالمطار أو بالفندق، من قبل السلطات الصحية المختصة.</span></p>\r\n</div>\r\n', 0x6a616d6133612d363162393134646333323765342d6165726f706f72742d312e77656270, '2021-12-14 22:04:12', '3', 'شرعت طائرات الخطوط الملكية المغربية في الاستعداد لتنظيم رحلات السفر صوب بلدان العالم، بعد أسابيع عطالة قسرية فرضتها سياقات جائحة كوفيد19 ومتحور أوميكرون.'),
(55, 'هسبريس تنال جائزة الصحافة', '<p style=\"text-align:center\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/W0qrPTYJfes\" width=\"640\"></iframe></p>\r\n', 0x6a616d6133612d363162393135343234353333342d6d617872657364656661756c742e77656270, '2021-12-14 22:05:54', '7', 'هسبريس تنال جائزة الصحافة'),
(56, 'احتجاج من أجل العيش الكريم', '<p style=\"text-align:center\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/NA9eo-brcZY\" width=\"640\"></iframe></p>\r\n', 0x6a616d6133612d363162393135623936323938632d6d617872657364656661756c742e77656270, '2021-12-14 22:07:53', '7', 'احتجاج من أجل العيش الكريم'),
(57, 'أخنوش يتفقد عربات التيلفريك', '<p style=\"text-align:center\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/t-_TUd6IJ1g\" width=\"640\"></iframe></p>\r\n', 0x6a616d6133612d363162393136323366336537392d6d617872657364656661756c742e77656270, '2021-12-14 22:09:40', '7', 'أخنوش يتفقد عربات التيلفريك'),
(58, 'مشروع ضخم لشركة \"ريضال\"', '<p style=\"text-align:center\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/6mG5o97rYJk\" width=\"640\"></iframe></p>\r\n', 0x6a616d6133612d363162393136363837373330372d6d617872657364656661756c742e77656270, '2021-12-14 22:10:48', '7', 'مشروع ضخم لشركة \"ريضال\"'),
(59, 'اتفاق المجلس الاقتصادي والمستشارين', '<p style=\"text-align:center\"><iframe allowfullscreen=\"\" frameborder=\"0\" height=\"360\" src=\"https://www.youtube.com/embed/7opYHG0cyy0\" width=\"640\"></iframe></p>\r\n', 0x6a616d6133612d363162393136613134386630362d6d617872657364656661756c742e77656270, '2021-12-14 22:11:45', '7', 'اتفاق المجلس الاقتصادي والمستشارين');

